import React, { useState } from 'react';
import axios from 'axios';
import './view.css';

const Output = () => {
  const [viewOption, setViewOption] = useState('monthly');
  const [dataOption, setDataOption] = useState('circle');
  const [circle, setCircle] = useState('');
  const [division, setDivision] = useState('');
  const [subdivision, setSubdivision] = useState('');

  const handleViewOptionChange = (e) => {
    setViewOption(e.target.value);
  };

  const handleDataOptionChange = (e) => {
    setDataOption(e.target.value);
  };

  const handleCircleChange = (e) => {
    setCircle(e.target.value);
  };

  const handleDivisionChange = (e) => {
    setDivision(e.target.value);
  };

  const handleSubdivisionChange = (e) => {
    setSubdivision(e.target.value);
  };

  const handleGetData = () => {
    // Prepare the API endpoint URL based on the selected options
    let apiUrl = '/api/data'; // Replace this with your actual API endpoint URL
    if (viewOption === 'monthly') {
      apiUrl += '/monthly';
    } else {
      apiUrl += '/yearly';
    }

    if (dataOption === 'circle') {
      apiUrl += `/circle/${circle}`;
    } else if (dataOption === 'division') {
      apiUrl += `/division/${circle}/${division}`;
    } else if (dataOption === 'subdivision') {
      apiUrl += `/subdivision/${circle}/${division}/${subdivision}`;
    }

    // Make the API call using axios
    axios
      .get(apiUrl)
      .then((response) => {
        // Handle the response data here
        console.log(response.data); // This will log the data returned from the API
        // Update the state with the fetched data if necessary
      })
      .catch((error) => {
        console.error('Error:', error); // Log the error if something went wrong
        // Optionally, you can handle the error or notify the user
      });
  };

  return (
    <div className="container">
      <h3>View Data</h3>
      <div className="radio-group">
        {/* Monthly and Yearly options in the same line */}
        <label>
          <input
            type="radio"
            value="monthly"
            checked={viewOption === 'monthly'}
            onChange={handleViewOptionChange}
          />
          View Monthly Data
        </label>
        <label>
          <input
            type="radio"
            value="yearly"
            checked={viewOption === 'yearly'}
            onChange={handleViewOptionChange}
          />
          View Yearly Data
        </label>
      </div>

      <div className="input-group">
        {/* Choose Circle, Division, and Subdivision options in the same line */}
        <label>
          <input
            type="radio"
            value="circle"
            checked={dataOption === 'circle'}
            onChange={handleDataOptionChange}
          />
          Choose Circle
        </label>
        <label>
          <input
            type="radio"
            value="division"
            checked={dataOption === 'division'}
            onChange={handleDataOptionChange}
          />
          Choose Division
        </label>
        <label>
          <input
            type="radio"
            value="subdivision"
            checked={dataOption === 'subdivision'}
            onChange={handleDataOptionChange}
          />
          Choose Subdivision
        </label>
      </div>

      {/* Display the fetched data here */}
      {/* For example, create a table to display the data */}
      <div className="button-container">
        <button onClick={handleGetData}>Get Data</button>
      </div>
    </div>
  );
};

export default Output;
