import React, { useState } from 'react';
import axios from 'axios';

const Output = () => {
  const [formData, setFormData] = useState({
    month: '',
    year: '',
    circle: '',
  });

  const [outputData, setOutputData] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Fetch data from the backend based on the selected inputs
    axios
      .get(`/getData?circle=${formData.circle}&year=${formData.year}&month=${formData.month}`) // Replace '/getData' with the appropriate backend endpoint
      .then((response) => {
        setOutputData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error); // Log the error if something went wrong
        // Optionally, you can handle the error or notify the user
      });
  };

  return (
    <div className="output-container">
      <h1 className='makesure'>View Data</h1>
     <div className='dsdc'>
     <form onSubmit={handleSubmit}>
        <div className='dsdc'>
          <label htmlFor="circle">Circle:</label>
          <input
            type="text"
            id="circle"
            name="circle"
            value={formData.circle}
            onChange={handleInputChange}
            placeholder="Enter Circle Name"
          />
        </div>
        <div>
          <label htmlFor="month">Month:</label>
          <select
            id="month"
            name="month"
            value={formData.month}
            onChange={handleInputChange}
          >
            <option value="">Select Month</option>
            {/* Add other month options here */}
          </select>
        </div>
        <div>
          <label htmlFor="year">Year:</label>
          <input
            type="number"
            id="year"
            name="year"
            value={formData.year}
            onChange={handleInputChange}
            placeholder="Enter Year"
          />
        </div>
        <button type="submit">Get Output</button>
      </form>
     </div>
      {outputData && (
        <div className="output-data">
          {/* Display the output data here */}
          <pre>{JSON.stringify(outputData, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default Output;
