import './SimpleForm.css';
import React, { useState, useRef, useCallback, useEffect } from 'react';
//import axios from 'axios';

const SubmissionContent = ({ name, division, subdivision }) => {
    const [formData, setFormData] = useState({

          field1: '',
          field2: '',
          field3: '',
          field4: '',
          field5: '',
          field6: '',
          field7: '',
          field8: '', // Month field
          field9: '',
          field10: '',

      });



  const [repeatCount, setRepeatCount] = useState(1);

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [index]: {
        ...prevData[index],
        [name]: value,
      },
    }));
  };

  const handleMonthChange = (e, index) => {
    const { value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [index]: {
        ...prevData[index],
        field8: value,
      },
    }));
  };

  const handleSubmit = () => {

    console.log('Form Data:', formData);


 // Transform formData into an array of objects
 const formDataArray = Object.keys(formData).map((key) => formData[key]);

 // Make an HTTP request to the backend API with the form data
//  axios.post('/api/form', { formData: formDataArray })
//    .then((response) => {
//      // Handle the response from the backend (optional)
//      console.log('Form data submitted successfully');
//    })
//    .catch((error) => {
//      console.error('Error submitting form data:', error);
//    });
};

  const handleRepeat = () => {
    setRepeatCount((prevCount) => prevCount + 1);
  };

  const renderFormFields = () => {
    const formFields = [];
    for (let i = 0; i < repeatCount; i++) {
      formFields.push(
        <div key={i}>
          <p>Circle: {name}</p>
          <p>Division: {division}</p>
          <p>Sub-Division: {subdivision}</p>

          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor={`field8-${i}`}>Month:</label>
              <select
                type="text"
                id={`field8-${i}`}
                name={`field8-${i}`}
                value={formData[i]?.field8 || ''}
                onChange={(e) => handleMonthChange(e, i)}
          >
            <option value="">Select Month</option>
              <option value="January">January</option>
              <option value="Febuary">Febuary</option>
              <option value="March">March</option>
              <option value="April">April</option>
              <option value="May">May</option>
              <option value="June">June</option>
              <option value="July">July</option>
              <option value="August">August</option>
              <option value="September">September</option>
              <option value="October">October</option>
              <option value="November">November</option>
              <option value="December">December</option>
            </select></div>


            <label htmlFor={`field9-${i}`}>Year:</label>
                <input
                  type="number"
                  id={`field9-${i}`}
                  name={`field9-${i}`}
                  value={formData[i]?.field9 || ''}
                  onChange={(e) => handleInputChange(e, i)}
                  placeholder="Enter Year"
                />

            <label htmlFor={`field1-${i}`}>Injection:</label>
                <input
                  type="number"
                  id={`field1-${i}`}
                  name={`field1-${i}`}
                  value={formData[i]?.field1 || ''}
                  onChange={(e) => handleInputChange(e, i)}
                  placeholder="Enter Injection value"
                />
              <label htmlFor={`field2-${i}`}>Unit Billed:</label>
                <input
                  type="number"
                  id={`field2-${i}`}
                  name={`field2-${i}`}
                  value={formData[i]?.field2 || ''}
                  onChange={(e) => handleInputChange(e, i)}
                  placeholder="Enter Unit Billed value"
                />



            <label htmlFor={`field3-${i}`}>Total IRCA Collection:</label>
              <input
                type="number"
                id={`field3-${i}`}
                  name={`field3-${i}`}
                value={formData[i]?.field3 || ''}
                onChange={(e) => handleInputChange(e, i)}
                placeholder="Enter Total IRCA Collection value"
              />



            <label htmlFor={`field4-${i}`}>Current Demand IRCA:</label>
              <input
                type="number"
                iid={`field4-${i}`}
                name={`field4-${i}`}
                value={formData[i]?.field4 || ''}
                onChange={(e) => handleInputChange(e, i)}
                placeholder="Enter Current Demand IRCA value"
              />



            <label htmlFor={`field5-${i}`}>No of Consumers:</label>
              <input
                type="number"
                id={`field5-${i}`}
                  name={`field5-${i}`}
                value={formData[i]?.field5 || ''}
                onChange={(e) => handleInputChange(e, i)}
                placeholder="Enter No of Consumers in a sub-division"
              />



            <label htmlFor={`field6-${i}`}>No of Bill Served:</label>
              <input
                type="number"
                id={`field6-${i}`}
                name={`field6-${i}`}
                value={formData[i]?.field6 || ''}
                onChange={(e) => handleInputChange(e, i)}
                placeholder="Enter No of Bill Served in a sub-division"
              />



          <label htmlFor={`field7-${i}`}>Total Area:</label>
              <input
                type="number"
                id={`field7-${i}`}
                name={`field7-${i}`}
                value={formData[i]?.field7 || ''}
                onChange={(e) => handleInputChange(e, i)}
                placeholder="Enter Total Area"
              />

            </form>
          </div>
        );
      }
      return formFields;
    };

    // Ref for the last form field container
  const lastFieldRef = useRef(null);
  const [isLoading, setIsLoading] = useState(false);

  const observerCallback = useCallback((entries) => {
    const target = entries[0];
    if (target.isIntersecting && !isLoading) {
      // Load more form fields when the last field comes into view
      setIsLoading(true);
      setRepeatCount((prevCount) => prevCount + 1);
    }
  }, [isLoading]);

  // Intersection observer
  const observer = useRef(
    new IntersectionObserver(observerCallback, {
      root: null,
      rootMargin: '10px',
      threshold: 0.1,
    })
  );

  // Attach the observer to the last form field container
  useEffect(() => {
    const { current: currentObserver } = observer;
    if (lastFieldRef.current) {
      currentObserver.observe(lastFieldRef.current);
    }
    return () => currentObserver.disconnect();
  }, [observer]);


  return (
    <div className="submission-content-container">
      <div className="submission-content">
        {/* Repeat the input fields */}
        {renderFormFields()}

        {/* "Add More" button */}
        <button type="button" className='addMore' onClick={handleRepeat}>
          Add More
        </button>

        <button type="submit" onClick={handleSubmit}>Submit</button>


        {/* Loading indicator */}
        {isLoading && <p>Loading...</p>}
      </div>
    </div>
  );
};

export default SubmissionContent;
