  //     subDivisionbilling_efficiency_perMonth !== '' &&
  //     subDivisionCollectionEfficiencyMonthly !== ''
  //   ) {
  //     // Convert the strings to numbers
  //     const billingEfficiencyValue = Number(subDivisionbilling_efficiency_perMonth);
  //     const collectionEfficiencyValue = Number(subDivisionCollectionEfficiencyMonthly);

  //     // Check if the conversion to numbers was successful
  //     if (!isNaN(billingEfficiencyValue) && !isNaN(collectionEfficiencyValue)) {
  //       // Perform the calculation and return the result
